import React, { useState, useEffect, useMemo } from 'react';
import AdminPanel from './components/AdminPanel';
import DetailModal from './components/DetailModal';
import SummaryCard from './components/SummaryCard';
import PromiseCard from './components/PromiseCard';
import { supabase } from './supabaseClient';

export const STATUS_OPTIONS = ['Kept', 'In Progress', 'Broken', 'Not Started'];
export const STATUS_COLORS = {
  'Kept': 'bg-green-100 text-green-800',
  'In Progress': 'bg-yellow-100 text-yellow-800',
  'Broken': 'bg-red-100 text-red-800',
  'Not Started': 'bg-gray-100 text-gray-800'
};

const ADMIN_PASSWORD = 'govtrack2025';

export default function App() {
  const [promises, setPromises] = useState([]);
  const [search, setSearch] = useState('');
  const [filterCategory, setFilterCategory] = useState('All');
  const [selected, setSelected] = useState(null);
  const [adminMode, setAdminMode] = useState(false);
  const [editItem, setEditItem] = useState(null);
  const [loginMode, setLoginMode] = useState(false);
  const [password, setPassword] = useState('');
  const [loginError, setLoginError] = useState('');

  // Load promises from Supabase
  useEffect(() => {
    async function loadData() {
      const { data, error } = await supabase
        .from('promises')
        .select('*')
        .order('id', { ascending: true });
      if (error) console.error('Error fetching promises:', error);
      else setPromises(data);
    }
    loadData();
  }, []);

  const categories = useMemo(() => ['All', ...new Set(promises.map(p => p.category))], [promises]);
  const counts = useMemo(() => {
    const c = { 'Kept': 0, 'In Progress': 0, 'Broken': 0, 'Not Started': 0 };
    promises.forEach(p => { if (c[p.status] !== undefined) c[p.status] += 1; });
    return c;
  }, [promises]);
  const filtered = useMemo(() => {
    return promises.filter(p => {
      if (filterCategory !== 'All' && p.category !== filterCategory) return false;
      if (!search.trim()) return true;
      const s = search.toLowerCase();
      return p.text.toLowerCase().includes(s)
        || p.category.toLowerCase().includes(s)
        || (p.source && p.source.toLowerCase().includes(s));
    });
  }, [promises, search, filterCategory]);

  // Save or update a promise in Supabase
  async function savePromise(data) {
    if (data.id) {
      const { error } = await supabase.from('promises')
        .update({
          text: data.text,
          category: data.category,
          source: data.source,
          status: data.status,
          last_updated: data.lastUpdated,
          updates: data.updates
        })
        .eq('id', data.id);
      if (error) console.error('Update error:', error);
      else setPromises(prev => prev.map(p => p.id === data.id ? data : p));
    } else {
      const { data: newRows, error } = await supabase.from('promises')
        .insert([{
          text: data.text,
          category: data.category,
          source: data.source,
          status: data.status,
          last_updated: data.lastUpdated,
          updates: data.updates
        }])
        .select();
      if (error) console.error('Insert error:', error);
      else setPromises(prev => [...prev, newRows[0]]);
    }
    setEditItem(null);
  }

  async function handleDelete(id) {
    const { error } = await supabase.from('promises').delete().eq('id', id);
    if (error) console.error('Delete error:', error);
    else setPromises(prev => prev.filter(p => p.id !== id));
  }

  function handleLogin(e) {
    e.preventDefault();
    if (password === ADMIN_PASSWORD) {
      setAdminMode(true);
      setLoginMode(false);
      setPassword('');
      setLoginError('');
    } else setLoginError('Incorrect password.');
  }

  return (
    <div className="min-h-screen bg-gray-50 text-gray-900">
      {/* HEADER */}
      <header className="bg-white shadow-md">
        <div className="max-w-6xl mx-auto px-4 py-6 flex flex-col md:flex-row items-center justify-between">
          <div className="flex items-center mb-4 md:mb-0">
            <div className="w-12 h-12 bg-gray-100 rounded-full flex items-center justify-center shadow-md mr-3">
              <span className="text-gray-700 font-bold text-lg">NL</span>
            </div>
            <div>
              <h1 className="text-2xl font-bold text-gray-900">Newfoundland & Labrador Platform Tracking</h1>
              <p className="text-sm text-gray-600">Keeping NL Governments Accountable</p>
            </div>
          </div>
          <nav className="space-x-4 text-sm text-gray-600">
            {!adminMode && !loginMode && (
              <button onClick={() => setLoginMode(true)} className="underline hover:text-gray-900">
                Admin Login
              </button>
            )}
            {adminMode && (
              <button onClick={() => setAdminMode(false)} className="underline hover:text-gray-900">
                Exit Admin
              </button>
            )}
          </nav>
        </div>
      </header>

      <main className="max-w-6xl mx-auto px-4 py-8">
        {!adminMode && !loginMode && (
          <>
            {/* SUMMARY CARDS + SEARCH/FILTER */}
            <section className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-8">
              <div className="md:col-span-3 bg-white rounded-lg p-6 shadow-sm">
                <h2 className="text-lg font-medium mb-2">Campaign Promise Tracker</h2>
                <div className="mt-4 flex flex-wrap gap-3">
                  {STATUS_OPTIONS.map(st => (
                    <SummaryCard
                      key={st}
                      label={st}
                      value={counts[st]}
                      className={`${STATUS_COLORS[st]} px-3 py-2 rounded shadow-md text-sm font-semibold`}
                    />
                  ))}
                </div>
              </div>
              <div className="bg-white rounded-lg p-6 shadow-sm">
                <input
                  value={search}
                  onChange={e => setSearch(e.target.value)}
                  placeholder="Search..."
                  className="w-full p-2 border rounded mb-3"
                />
                <select
                  value={filterCategory}
                  onChange={e => setFilterCategory(e.target.value)}
                  className="w-full p-2 border rounded"
                >
                  {categories.map(c => (<option key={c}>{c}</option>))}
                </select>
              </div>
            </section>

            {/* PROMISES LIST */}
            <section className="grid grid-cols-1 lg:grid-cols-3 gap-4">
              <div className="lg:col-span-2 space-y-4">
                {filtered.map(p => (
                  <PromiseCard key={p.id} p={p} onClick={() => setSelected(p)} />
                ))}
              </div>
              <aside>{/* Charts/Progress placeholder */}</aside>
            </section>
          </>
        )}

        {loginMode && (
          <div className="max-w-md mx-auto bg-white p-6 rounded-lg shadow-sm">
            <h2 className="text-lg font-semibold mb-4">Admin Login</h2>
            <form onSubmit={handleLogin} className="space-y-3">
              <input
                type="password"
                value={password}
                onChange={e => setPassword(e.target.value)}
                placeholder="Enter admin password"
                className="w-full p-2 border rounded"
              />
              {loginError && <div className="text-red-600 text-sm">{loginError}</div>}
              <div className="flex gap-2">
                <button type="submit" className="px-4 py-2 bg-emerald-500 text-white rounded">Login</button>
                <button type="button" onClick={() => { setLoginMode(false); setPassword(''); }} className="text-sm underline">Cancel</button>
              </div>
            </form>
          </div>
        )}

        {adminMode && (
          <AdminPanel
            promises={promises}
            onEdit={setEditItem}
            onDelete={handleDelete}
            onSave={savePromise}
            editItem={editItem}
            onCancel={() => setEditItem(null)}
          />
        )}
      </main>

      {selected && <DetailModal selected={selected} onClose={() => setSelected(null)} />}
    </div>
  );
}
