import React, { useState, useEffect } from 'react';
import { STATUS_COLORS } from '../constants';

export default function AdminPanel({ promises, onEdit, onDelete, onSave, editItem, onCancel }) {
  const [form, setForm] = useState({
    text: '',
    category: '',
    source: '',
    status: 'Not Started',
    lastUpdated: '',
    updates: ''
  });
  const [addingNew, setAddingNew] = useState(false);

  useEffect(() => {
    if (editItem) {
      setForm({
        text: editItem.text || '',
        category: editItem.category || '',
        source: editItem.source || '',
        status: editItem.status || 'Not Started',
        lastUpdated: editItem.last_updated || '',
        updates: editItem.updates || ''
      });
      setAddingNew(true);
    } else {
      setForm({
        text: '',
        category: '',
        source: '',
        status: 'Not Started',
        lastUpdated: '',
        updates: ''
      });
      setAddingNew(false);
    }
  }, [editItem]);

  const handleChange = (field, value) => {
    setForm(prev => ({ ...prev, [field]: value }));
  };

  const handleSave = () => {
    const dataToSave = editItem ? { ...editItem, ...form } : form;
    onSave(dataToSave);
    setAddingNew(false);
  };

  const handleAddNew = () => {
    setForm({
      text: '',
      category: '',
      source: '',
      status: 'Not Started',
      lastUpdated: '',
      updates: ''
    });
    setAddingNew(true);
  };

  return (
    <div className="space-y-6">
      {/* PROMISE LIST */}
      <div className="flex justify-between items-center mb-4">
        <h2 className="text-2xl font-semibold">Admin Panel</h2>
        <button
          onClick={handleAddNew}
          className="px-4 py-2 bg-emerald-500 text-white rounded-lg hover:bg-emerald-600 shadow"
        >
          Add Promise
        </button>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {promises.length === 0 && <p className="text-gray-500 col-span-full">No promises found.</p>}
        {promises.map(p => (
          <div key={p.id} className="bg-white rounded-2xl p-5 shadow-md border border-gray-200 flex flex-col justify-between">
            <div>
              <h3 className="font-semibold text-gray-800">{p.text}</h3>
              <p className="text-sm text-gray-500">{p.category}</p>
              <p className="text-sm text-gray-500">{p.source}</p>
              <p className="text-sm mt-1">
                Status: <span className={`font-semibold px-2 py-1 rounded ${STATUS_COLORS[p.status] || 'bg-gray-100 text-gray-800'}`}>{p.status}</span>
              </p>
            </div>
            <div className="mt-2 flex gap-2">
              <button
                onClick={() => onEdit(p)}
                className="px-3 py-1 bg-blue-500 text-white text-sm rounded-lg hover:bg-blue-600 shadow"
              >
                Edit
              </button>
              <button
                onClick={() => onDelete(p.id)}
                className="px-3 py-1 bg-red-500 text-white text-sm rounded-lg hover:bg-red-600 shadow"
              >
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>

      {/* ADD / EDIT FORM */}
      {addingNew && (
        <div className="mt-6 bg-gray-50 p-6 rounded-2xl shadow-lg max-w-3xl mx-auto border border-gray-200">
          <h2 className="text-xl font-semibold mb-4">{editItem ? 'Edit Promise' : 'Add Promise'}</h2>
          <div className="space-y-4">
            <textarea
              value={form.text}
              onChange={e => handleChange('text', e.target.value)}
              placeholder="Promise text"
              className="w-full p-3 border rounded-lg resize-none shadow-sm"
              rows={3}
            />
            <input
              value={form.category}
              onChange={e => handleChange('category', e.target.value)}
              placeholder="Category"
              className="w-full p-3 border rounded-lg shadow-sm"
            />
            <input
              value={form.source}
              onChange={e => handleChange('source', e.target.value)}
              placeholder="Source / Link"
              className="w-full p-3 border rounded-lg shadow-sm"
            />
            <textarea
              value={form.updates}
              onChange={e => handleChange('updates', e.target.value)}
              placeholder="Updates / Notes"
              className="w-full p-3 border rounded-lg resize-none shadow-sm"
              rows={2}
            />
            <select
              value={form.status}
              onChange={e => handleChange('status', e.target.value)}
              className="w-full p-3 border rounded-lg shadow-sm"
            >
              <option>Kept</option>
              <option>In Progress</option>
              <option>Broken</option>
              <option>Not Started</option>
            </select>
            <div className="flex justify-end gap-3 mt-4">
              <button
                onClick={handleSave}
                className="px-5 py-2 bg-emerald-500 text-white rounded-lg hover:bg-emerald-600 shadow"
              >
                Save
              </button>
              <button
                onClick={() => { onCancel(); setAddingNew(false); }}
                className="px-5 py-2 bg-gray-300 text-gray-800 rounded-lg hover:bg-gray-400 shadow"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
