import React, { useState, useEffect } from 'react';
import { STATUS_COLORS } from '../constants';

export default function AdminPanel({ promises, onEdit, onDelete, onSave, editItem, onCancel }) {
  const [form, setForm] = useState({
    text: '',
    category: '',
    source: '',
    status: 'Not Started',
    lastUpdated: '',
    updates: ''
  });

  useEffect(() => {
    if (editItem) {
      setForm({
        text: editItem.text || '',
        category: editItem.category || '',
        source: editItem.source || '',
        status: editItem.status || 'Not Started',
        lastUpdated: editItem.last_updated || '',
        updates: editItem.updates || ''
      });
    } else {
      setForm({
        text: '',
        category: '',
        source: '',
        status: 'Not Started',
        lastUpdated: '',
        updates: ''
      });
    }
  }, [editItem]);

  const handleChange = (field, value) => {
    setForm(prev => ({ ...prev, [field]: value }));
  };

  const handleSave = () => {
    const dataToSave = editItem && editItem.id ? { ...editItem, ...form } : form;
    onSave(dataToSave);
  };

  return (
    <div className="space-y-8">
      {/* ADD / EDIT PROMISE FORM */}
      <div className="bg-gray-50 dark:bg-gray-800 rounded-2xl p-6 shadow-lg max-w-3xl mx-auto border border-gray-200 dark:border-gray-700">
        <h2 className="text-xl font-semibold mb-5 text-gray-900 dark:text-gray-100">
          {editItem && editItem.id ? 'Edit Promise' : 'Add Promise'}
        </h2>
        <div className="space-y-4">
          <textarea
            value={form.text}
            onChange={e => handleChange('text', e.target.value)}
            placeholder="Promise text"
            className="w-full p-3 border rounded-lg dark:bg-gray-700 dark:border-gray-600 dark:text-gray-100"
            rows={3}
          />
          <input
            value={form.category}
            onChange={e => handleChange('category', e.target.value)}
            placeholder="Category"
            className="w-full p-3 border rounded-lg dark:bg-gray-700 dark:border-gray-600 dark:text-gray-100"
          />
          <input
            value={form.source}
            onChange={e => handleChange('source', e.target.value)}
            placeholder="Source / Link"
            className="w-full p-3 border rounded-lg dark:bg-gray-700 dark:border-gray-600 dark:text-gray-100"
          />
          <textarea
            value={form.updates}
            onChange={e => handleChange('updates', e.target.value)}
            placeholder="Updates / Notes"
            className="w-full p-3 border rounded-lg dark:bg-gray-700 dark:border-gray-600 dark:text-gray-100"
            rows={2}
          />
          <select
            value={form.status}
            onChange={e => handleChange('status', e.target.value)}
            className="w-full p-3 border rounded-lg dark:bg-gray-700 dark:border-gray-600 dark:text-gray-100"
          >
            <option>Kept</option>
            <option>In Progress</option>
            <option>Broken</option>
            <option>Not Started</option>
          </select>
          <div className="flex justify-end gap-3 mt-4">
            <button
              onClick={handleSave}
              className="px-5 py-2 bg-emerald-500 text-white rounded-xl shadow hover:bg-emerald-600 transition"
            >
              Save
            </button>
            {editItem && (
              <button
                onClick={onCancel}
                className="px-5 py-2 bg-gray-200 dark:bg-gray-700 text-gray-900 dark:text-gray-100 rounded-xl shadow hover:bg-gray-300 dark:hover:bg-gray-600 transition"
              >
                Cancel
              </button>
            )}
          </div>
        </div>
      </div>

      {/* PROMISE LIST */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {promises.map(p => (
          <div key={p.id} className="bg-white dark:bg-gray-800 rounded-2xl p-5 shadow-md border border-gray-200 dark:border-gray-700 flex flex-col justify-between transition hover:shadow-lg">
            <div className="space-y-2">
              <h3 className="font-semibold text-gray-900 dark:text-gray-100">{p.text}</h3>
              <p className="text-sm text-gray-500 dark:text-gray-400">{p.category}</p>
              {p.source && <p className="text-sm text-gray-500 dark:text-gray-400">{p.source}</p>}
              <p className="text-sm mt-1">
                Status: <span className={`${STATUS_COLORS[p.status] || 'text-gray-800'}`}>{p.status}</span>
              </p>
            </div>
            <div className="mt-3 flex gap-2">
              <button
                onClick={() => onEdit(p)}
                className="px-3 py-1 bg-blue-500 text-white text-sm rounded-lg hover:bg-blue-600 transition"
              >
                Edit
              </button>
              <button
                onClick={() => onDelete(p.id)}
                className="px-3 py-1 bg-red-500 text-white text-sm rounded-lg hover:bg-red-600 transition"
              >
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
