import React, { useState, useEffect, useMemo } from 'react';
import AdminPanel from './components/AdminPanel';
import DetailModal from './components/DetailModal';
import SummaryCard from './components/SummaryCard';
import PromiseCard from './components/PromiseCard';
import { supabase } from './supabaseClient';
import { STATUS_OPTIONS, STATUS_COLORS } from './constants';

import {
  ResponsiveContainer,
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  Cell
} from 'recharts';

const ADMIN_PASSWORD = 'govtrack2025';

export default function App() {
  const [promises, setPromises] = useState([]);
  const [search, setSearch] = useState('');
  const [filterCategory, setFilterCategory] = useState('All');
  const [selected, setSelected] = useState(null);
  const [adminMode, setAdminMode] = useState(false);
  const [editItem, setEditItem] = useState(null);
  const [loginMode, setLoginMode] = useState(false);
  const [password, setPassword] = useState('');
  const [loginError, setLoginError] = useState('');

  // Load promises from Supabase
  useEffect(() => {
    async function loadData() {
      const { data, error } = await supabase
        .from('promises')
        .select('*')
        .order('id', { ascending: true });
      if (error) {
        console.error('Error fetching promises:', error);
      } else {
        setPromises(data);
      }
    }
    loadData();
  }, []);

  const categories = useMemo(() => ['All', ...new Set(promises.map(p => p.category))], [promises]);

  const counts = useMemo(() => {
    const c = { 'Kept': 0, 'In Progress': 0, 'Broken': 0, 'Not Started': 0 };
    promises.forEach(p => {
      if (c[p.status] !== undefined) c[p.status] += 1;
    });
    return c;
  }, [promises]);

  const filtered = useMemo(() => {
    return promises.filter(p => {
      if (filterCategory !== 'All' && p.category !== filterCategory) return false;
      if (!search.trim()) return true;
      const s = search.toLowerCase();
      return p.text.toLowerCase().includes(s)
        || p.category.toLowerCase().includes(s)
        || (p.source && p.source.toLowerCase().includes(s));
    });
  }, [promises, search, filterCategory]);

  // Data for charts
  const statusData = useMemo(() => STATUS_OPTIONS.map(st => ({ status: st, count: counts[st] || 0 })), [counts]);
  const categoryCounts = useMemo(() => {
    const catCount = {};
    promises.forEach(p => {
      if (p.category) catCount[p.category] = (catCount[p.category] || 0) + 1;
    });
    return Object.entries(catCount).map(([category, count]) => ({ category, count }));
  }, [promises]);

  async function savePromise(data) {
    if (data.id) {
      const { error } = await supabase.from('promises')
        .update({
          text: data.text,
          category: data.category,
          source: data.source,
          status: data.status,
          last_updated: data.lastUpdated,
          updates: data.updates
        })
        .eq('id', data.id);
      if (error) {
        console.error('Update error:', error);
      } else {
        setPromises(prev => prev.map(p => p.id === data.id ? data : p));
      }
    } else {
      const { data: newRows, error } = await supabase.from('promises')
        .insert([{
          text: data.text,
          category: data.category,
          source: data.source,
          status: data.status,
          last_updated: data.lastUpdated,
          updates: data.updates
        }])
        .select();
      if (error) {
        console.error('Insert error:', error);
      } else {
        setPromises(prev => [...prev, newRows[0]]);
      }
    }
    setEditItem(null);
  }

  async function handleDelete(id) {
    const { error } = await supabase.from('promises').delete().eq('id', id);
    if (error) {
      console.error('Delete error:', error);
    } else {
      setPromises(prev => prev.filter(p => p.id !== id));
    }
  }

  function handleLogin(e) {
    e.preventDefault();
    if (password === ADMIN_PASSWORD) {
      setAdminMode(true);
      setLoginMode(false);
      setPassword('');
      setLoginError('');
    } else {
      setLoginError('Incorrect password.');
    }
  }

  return (
    <div className="min-h-screen bg-gray-50 text-gray-900 dark:bg-gray-900 dark:text-gray-100">
      {/* HEADER */}
      <header className="sticky top-0 z-50 backdrop-blur-md bg-white/90 dark:bg-gray-900/80 border-b border-gray-200 dark:border-gray-700 shadow-sm">
        <div className="max-w-6xl mx-auto px-4 py-4 flex items-center justify-between">
          {/* Left: Logo + Title */}
          <div className="flex items-center gap-4">
            <div className="w-12 h-12 flex items-center justify-center bg-gray-100 dark:bg-gray-800 rounded-full shadow">
              <svg
                className="w-8 h-8 text-emerald-500 dark:text-emerald-400"
                viewBox="0 0 24 24"
                fill="none"
                stroke="currentColor"
                strokeWidth="2"
                strokeLinecap="round"
                strokeLinejoin="round"
              >
                <circle cx="12" cy="12" r="10" />
                <path d="M8 12l3 3 5-5" />
              </svg>
            </div>
            <div>
              <h1 className="text-2xl font-bold text-gray-900 dark:text-gray-100 tracking-tight">
                Newfoundland & Labrador Platform Tracking
              </h1>
              <p className="text-sm text-gray-500 dark:text-gray-300 mt-1 font-medium">
                Keeping NL Governments Accountable
              </p>
            </div>
          </div>

          {/* Right: Navigation */}
          <nav className="flex items-center gap-3 text-sm font-medium">
            {!adminMode && !loginMode && (
              <button
                onClick={() => setLoginMode(true)}
                className="px-3 py-1.5 rounded-md text-gray-700 dark:text-gray-200 hover:text-emerald-500 dark:hover:text-emerald-400 hover:bg-emerald-50 dark:hover:bg-emerald-900 transition"
              >
                Admin Login
              </button>
            )}
            {adminMode && (
              <button
                onClick={() => setAdminMode(false)}
                className="px-3 py-1.5 rounded-md text-gray-700 dark:text-gray-200 hover:text-red-700 dark:hover:text-red-400 hover:bg-red-50 dark:hover:bg-red-900 transition"
              >
                Exit Admin
              </button>
            )}
          </nav>
        </div>
        <div className="h-1 bg-gradient-to-r from-emerald-500 via-teal-400 to-emerald-500 dark:from-emerald-400 dark:via-teal-400 dark:to-emerald-400"></div>
      </header>

      {/* MAIN CONTENT */}
      <main className="max-w-6xl mx-auto px-4 py-8">
        {!adminMode && !loginMode && (
          <>
            {/* SUMMARY + SEARCH */}
            <section className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-8">
              <div className="md:col-span-3 bg-white rounded-lg p-6 shadow-sm">
                <h2 className="text-lg font-medium mb-2">Campaign Promise Tracker</h2>
                <div className="mt-4 flex flex-wrap gap-3">
                  {STATUS_OPTIONS.map(st => (
                    <SummaryCard key={st} label={st} value={counts[st]} className={STATUS_COLORS[st]} />
                  ))}
                </div>
              </div>
              <div className="bg-white rounded-lg p-6 shadow-sm">
                <input
                  value={search}
                  onChange={e => setSearch(e.target.value)}
                  placeholder="Search..."
                  className="w-full p-2 border rounded mb-3"
                />
                <select
                  value={filterCategory}
                  onChange={e => setFilterCategory(e.target.value)}
                  className="w-full p-2 border rounded"
                >
                  {categories.map(c => (<option key={c}>{c}</option>))}
                </select>
              </div>
            </section>

            {/* PROMISES + CHARTS */}
            <section className="grid grid-cols-1 lg:grid-cols-3 gap-4">
              <div className="lg:col-span-2 space-y-4">
                {filtered.map(p => (
                  <PromiseCard key={p.id} p={p} onClick={() => setSelected(p)} />
                ))}
              </div>

              {/* ASIDE: Progress Charts */}
              <aside className="space-y-6 lg:col-span-1">
                {/* Progress by Status */}
                <div className="bg-white dark:bg-gray-800 p-4 rounded-lg shadow-sm">
                  <h3 className="text-lg font-semibold mb-2 text-gray-800 dark:text-gray-100">Progress by Status</h3>
                  <ResponsiveContainer width="100%" height={200}>
                    <BarChart data={statusData} layout="vertical" margin={{ top: 5, right: 10, bottom: 5, left: 10 }}>
                      <XAxis type="number" hide />
                      <YAxis type="category" dataKey="status" tick={{ fill: '#111827', fontWeight: 500 }} />
                      <Tooltip contentStyle={{ backgroundColor: '#111827', color: '#F9FAFB' }} />
                      <Bar dataKey="count">
                        {statusData.map((entry, index) => (
                          <Cell key={`cell-${index}`} fill={
                            entry.status === 'Kept' ? 'url(#gradGreen)'
                            : entry.status === 'In Progress' ? 'url(#gradTeal)'
                            : entry.status === 'Broken' ? 'url(#gradRed)'
                            : 'url(#gradGray)'
                          } />
                        ))}
                      </Bar>
                      <defs>
                        <linearGradient id="gradGreen" x1="0" y1="0" x2="1" y2="0">
                          <stop offset="0%" stopColor="#10B981" />
                          <stop offset="100%" stopColor="#6EE7B7" />
                        </linearGradient>
                        <linearGradient id="gradTeal" x1="0" y1="0" x2="1" y2="0">
                          <stop offset="0%" stopColor="#14B8A6" />
                          <stop offset="100%" stopColor="#5EEAD4" />
                        </linearGradient>
                        <linearGradient id="gradRed" x1="0" y1="0" x2="1" y2="0">
                          <stop offset="0%" stopColor="#EF4444" />
                          <stop offset="100%" stopColor="#FCA5A5" />
                        </linearGradient>
                        <linearGradient id="gradGray" x1="0" y1="0" x2="1" y2="0">
                          <stop offset="0%" stopColor="#9CA3AF" />
                          <stop offset="100%" stopColor="#D1D5DB" />
                        </linearGradient>
                      </defs>
                    </BarChart>
                  </ResponsiveContainer>
                </div>

                {/* Progress by Category */}
                <div className="bg-white dark:bg-gray-800 p-4 rounded-lg shadow-sm">
                  <h3 className="text-lg font-semibold mb-2 text-gray-800 dark:text-gray-100">Progress by Category</h3>
                  <ResponsiveContainer width="100%" height={200}>
                    <BarChart data={categoryCounts} layout="vertical" margin={{ top: 5, right: 10, bottom: 5, left: 10 }}>
                      <XAxis type="number" hide />
                      <YAxis type="category" dataKey="category" tick={{ fill: '#111827', fontWeight: 500 }} />
                      <Tooltip contentStyle={{ backgroundColor: '#111827', color: '#F9FAFB' }} />
                      <Bar dataKey="count" fill="url(#gradBlue)" />
                      <defs>
                        <linearGradient id="gradBlue" x1="0" y1="0" x2="1" y2="0">
                          <stop offset="0%" stopColor="#3B82F6" />
                          <stop offset="100%" stopColor="#93C5FD" />
                        </linearGradient>
                      </defs>
                    </BarChart>
                  </ResponsiveContainer>
                </div>
              </aside>
            </section>
          </>
        )}

        {/* LOGIN FORM */}
        {loginMode && (
          <div className="max-w-md mx-auto bg-white p-6 rounded-lg shadow-sm">
            <h2 className="text-lg font-semibold mb-4">Admin Login</h2>
            <form onSubmit={handleLogin} className="space-y-3">
              <input
                type="password"
                value={password}
                onChange={e => setPassword(e.target.value)}
                placeholder="Enter admin password"
                className="w-full p-2 border rounded"
              />
              {loginError && <div className="text-red-600 text-sm">{loginError}</div>}
              <div className="flex gap-2">
                <button type="submit" className="px-4 py-2 bg-emerald-500 text-white rounded">Login</button>
                <button type="button" onClick={() => { setLoginMode(false); setPassword(''); }} className="text-sm underline">Cancel</button>
              </div>
            </form>
          </div>
        )}

        {/* ADMIN PANEL */}
        {adminMode && (
          <AdminPanel
            promises={promises}
            onEdit={setEditItem}
            onDelete={handleDelete}
            onSave={savePromise}
            editItem={editItem}
            onCancel={() => setEditItem(null)}
          />
        )}
      </main>

      {selected && <DetailModal selected={selected} onClose={() => setSelected(null)} />}
    </div>
  );
}
